import React, { useState, Component } from "react";
import { TransitionGroup, CSSTransition } from "react-transition-group";
import Footer from "./Footer";

import clearSky from "../images/clear_sky.svg";
import fewClouds from "../images/few_clouds.svg";
import nightFewClouds from "../images/night_few_clouds.svg";
import nightClearSky from "../images/night_clear_sky.svg";
import scatteredClouds from "../images/scattered_clouds.svg";
import showerRain from "../images/shower_rain.svg";
import snow from "../images/snow.svg";
import thunderStorm from "../images/thunderstorm.svg";
import weatherLogo from "../images/weather-logo.svg";

const api = {
  key: "cb6c26f0569f865f8485400d1d8d011a",
  base: "https://api.openweathermap.org/data/2.5/",
};

const date = new Date();

const Main = () => {
  const [query, setQuery] = useState("");
  const [weather, setWeather] = useState({});

  const search = (evt) => {
    if (evt.key === "Enter") {
      fetch(`${api.base}weather?q=${query}&units=metric&APPID=${api.key}`)
        .then((res) => res.json())
        .then((result) => {
          setWeather(result);
          setQuery("");
        });
    }
  };

  const dateBuilder = (d) => {
    let months = [
      "January",
      "February",
      "March",
      "April",
      "May",
      "June",
      "July",
      "August",
      "September",
      "October",
      "November",
      "December",
    ];
    let days = [
      "Sunday",
      "Monday",
      "Tuesday",
      "Wednesday",
      "Thursday",
      "Friday",
      "Saturday",
    ];

    let day = days[d.getDay()];
    let date = d.getDate();
    let month = months[d.getMonth()];
    let year = d.getFullYear();
    let time = d.getHours() + ":" + d.getMinutes();

    return `${time} ${day}, ${date} ${month} ${year}`;
  };

  const switchImg = () => {
    if (date.getHours() < 6 || date.getHours() > 21) {
      switch (weather.weather[0].main) {
        case "Clear":
          return <img src={nightClearSky} alt="" />;
        case "Clouds":
          return <img src={nightFewClouds} alt="" />;
        case "Drizzle":
          return <img src={scatteredClouds} alt="" />;
        case "Rain":
          return <img src={showerRain} alt="" />;
        case "Thunderstorm":
          return <img src={thunderStorm} alt="" />;
        case "Snow":
          return <img src={snow} alt="" />;
        default:
          return <img src={scatteredClouds} alt="" />;
      }
    } else {
      switch (weather.weather[0].main) {
        case "Clear":
          return <img src={clearSky} alt="" />;
        case "Clouds":
          return <img src={fewClouds} alt="" />;
        case "Drizzle":
          return <img src={scatteredClouds} alt="" />;
        case "Rain":
          return <img src={showerRain} alt="" />;
        case "Thunderstorm":
          return <img src={thunderStorm} alt="" />;
        case "Snow":
          return <img src={snow} alt="" />;
        default:
          return <img src={scatteredClouds} alt="" />;
      }
    }
  };

  return (
    <div
      className={
        typeof weather.main != "undefined"
          ? weather.main.temp > 16
            ? "app warm"
            : "app"
          : "app"
      }
    >
      <main>
        <div className="container">
          <img className="weather-logo" src={weatherLogo} alt="" />
          {typeof weather.main != "undefined" ? (
            <div className="content">
              <TransitionGroup>
                <CSSTransition className="content-active">
                  <div className="temp-box">
                    <div className="temp">{Math.round(weather.main.temp)}</div>
                    <span className="deg">Â°c</span>
                  </div>
                  <div className="image-box">{switchImg()}</div>
                  <div className="location-box">
                    <div className="location">
                      {weather.name}, {weather.sys.country}
                    </div>
                    <div className="date">{dateBuilder(date)}</div>
                  </div>
                  <div className="weather">
                    {weather.weather[0].description}
                  </div>
                </CSSTransition>
              </TransitionGroup>
            </div>
          ) : (
            <div className="hint">write the name of the city in the search</div>
          )}
          <div className="search-box">
            <input
              type="text"
              placeholder="Search..."
              onChange={(e) => setQuery(e.target.value)}
              value={query}
              onKeyPress={search}
            />
          </div>
          <Footer></Footer>
        </div>
      </main>
    </div>
  );
};

export default Main;
