import React, { useState } from "react";
import "./App.sass";
import clearSky from "./images/clear_sky.svg";
import flewClouds from "./images/few_clouds.svg";
import nightFewClouds from "./images/night_few_clouds.svg";
import rain from "./images/rain.svg";
import scatteredClouds from "./images/scattered_clouds.svg";
import showerRain from "./images/shower_rain.svg";
import snow from "./images/snow.svg";
import thunderStorm from "./images/thunderstorm.svg";

const api = {
  key: "cb6c26f0569f865f8485400d1d8d011a",
  base: "https://api.openweathermap.org/data/2.5/",
};

function App() {
  const [query, setQuery] = useState("");
  const [weather, setWeather] = useState({});

  const search = (evt) => {
    if (evt.key === "Enter") {
      fetch(`${api.base}weather?q=${query}&units=metric&APPID=${api.key}`)
        .then((res) => res.json())
        .then((result) => {
          setWeather(result);
          setQuery("");
          console.log(result);
        });
    }
  };

  const dateBuilder = (d) => {
    let months = [
      "January",
      "February",
      "March",
      "April",
      "May",
      "June",
      "July",
      "August",
      "September",
      "October",
      "November",
      "December",
    ];
    let days = [
      "Sunday",
      "Monday",
      "Tuesday",
      "Wednesday",
      "Thursday",
      "Friday",
      "Saturday",
    ];

    let day = days[d.getDay()];
    let date = d.getDate();
    let month = months[d.getMonth()];
    let year = d.getFullYear();

    return `${day} ${date} ${month} ${year}`;
  };

  const switchImg = () => {
    switch (weather.weather[0].description) {
      case "clear sky":
        return <img src={clearSky} alt="" />;
      // case "overcast clouds":
      //   return <img src={ov} alt="" />;
      // case "overcast clouds":
      //   return <img src={} alt="" />;
      // case "overcast clouds":
      //   return <img src={} alt="" />;
      // case "overcast clouds":
      //   return <img src={} alt="" />;
      // case "overcast clouds":
      //   return <img src={} alt="" />;
      // case "overcast clouds":
      //   return <img src={} alt="" />;
      // case "overcast clouds":
      //   return <img src={} alt="" />;
      default:
        break;
    }
  };

  return (
    <div
      className={
        typeof weather.main != "undefined"
          ? weather.main.temp > 16
            ? "app warm"
            : "app"
          : "app"
      }
    >
      <main>
        <div className="container">
          {typeof weather.main != "undefined" ? (
            <div>
              <div className="location-box">
                <div className="date">{dateBuilder(new Date())}</div>
                <div className="location">
                  {weather.name}, {weather.sys.country}
                </div>
              </div>
              <div className="image-box">{switchImg()}</div>
              <div className="weather-box">
                <div className="temp">{Math.round(weather.main.temp)}Â°c</div>
                <div className="weather">{weather.weather[0].description}</div>
              </div>
            </div>
          ) : (
            ""
          )}
          <div className="search-box">
            <input
              type="text"
              placeholder="Search..."
              onChange={(e) => setQuery(e.target.value)}
              value={query}
              onKeyPress={search}
            />
          </div>
        </div>
      </main>
    </div>
  );
}

export default App;
